import { useState } from 'react';

const useValidator = (options) => {
    const [errors, setErrors] = useState({});

    // Tạo Map để tra cứu nhanh hơn
    const ruleMap = options.rules.reduce((map, rule) => {
        if (!map[rule.selector]) {
            map[rule.selector] = [];
        }
        map[rule.selector].push(rule);
        return map;
    }, {});

    const validateField = (fieldName, formData) => {
        const rules = ruleMap[fieldName] || [];
        for (let i = 0; i < rules.length; i++) {
            const message = rules[i].test(formData[fieldName], formData);
            if (message) {
                setErrors((prev) => ({
                    ...prev,
                    [fieldName]: message,
                }));
                break;
            }
        }
    };

    const clearError = (fieldName) => {
        setErrors((prev) => {
            const { [fieldName]: _, ...rest } = prev;
            return rest;
        });
    };

    const validateAll = (formData) => {
        const newErrors = {};
        options.rules.forEach((rule) => {
            const message = rule.test(formData[rule.selector], formData);
            if (message && !newErrors[rule.selector]) {
                newErrors[rule.selector] = message;
            }
        });
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    return {
        errors,
        validateField,
        clearError,
        validateAll,
    };
};
