import classNames from 'classnames/bind';
import { faCircleInfo, faUser, faGear, faPlus, faSignOut } from '@fortawesome/free-solid-svg-icons';
import { faBell } from '@fortawesome/free-regular-svg-icons';
// import HeadlessTippy from '@tippyjs/react/headless';
import Tippy from '@tippyjs/react';
import 'tippy.js/dist/tippy.css';

import styles from './Header.module.scss';
import images from '~/assets/images';
import Button from '~/components/Button';
import Image from '~/components/Image';
import { Link } from 'react-router-dom';
import Menu from '~/components/Popper/Menu';
import Search from '/Search';

const cx = classNames.bind(styles);

const MENU_ITEMS = [

    {
        icon: faUser,
        title: 'Mai Văn Lợi',
        to: '/vloi03',
        separate: true
    },
    {
        icon: faGear,
        title: 'Setting',
        to: '/setting'
    },
    {
        icon: faCircleInfo,
        title: 'Support',
        to: '/support'
    },
    {
        icon: faSignOut,
        title: 'Logout',
        to: '/login'
    }
]

function Header() {

    const currentUser = true

    return (
        <header className={cx('wrapper')}>
            <div className={cx('inner')}>
                <div className={cx('logo')}>
                    <Link to="/">
                        <img src={images.logo} alt="Forum " />
                        <h4 className={cx('logo-title')}>ForumLanguages</h4>
                    </Link>
                </div>

                <Search />

                <div className={cx('action')}>
                    {
                        currentUser ? (
                            <>
                                <Tippy content="Create new post" placement='bottom'>
                                    <Button to={'/upload'} primary round leftIcon={faPlus}>Create</Button>
                                </Tippy>
                                <Button className='notify' leftIcon={faBell} />
                                <Menu items={MENU_ITEMS}>
                                    <Image className={cx('user-avatar')} src='https://fullstack.edu.vn/assets/images/f8_avatar.png' />
                                </Menu>
                            </>
                        ) : (
                            <>
                                <Button to={'/login'} primary round >Login</Button>
                                <Button to={'/register'} primary round >Register</Button>
                            </>
                        )
                    }

                </div>
            </div>
        </header>
    );
}

export default Header;
