import { useState, useRef } from 'react';
import classNames from 'classnames/bind';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClose, faPlus, faSearch } from '@fortawesome/free-solid-svg-icons';
import { faBell } from '@fortawesome/free-regular-svg-icons';
// import HeadlessTippy from '@tippyjs/react/headless';
import Tippy from '@tippyjs/react';
import 'tippy.js/dist/tippy.css';

import styles from './Header.module.scss';
import images from '~/assets/images';
import Button from '~/components/Button';

const cx = classNames.bind(styles);

function Header() {

    const [searchValue, setSearchValue] = useState('');

    const currentUser = true
    const inputRef = useRef()

    const handleClear = () => {
        setSearchValue('')
    }

    return (
        <header className={cx('wrapper')}>
            <div className={cx('inner')}>
                <div className={cx('logo')}>
                    <a href="/">
                        <img src={images.logo} alt="Forum " />
                        <h4 className={cx('logo-title')}>ForumLanguages</h4>
                    </a>
                </div>

                <div className={cx('search')}>
                    <input
                        ref={inputRef}
                        value={searchValue}
                        onChange={(e) => setSearchValue(e.target.value)}
                        placeholder="Search posts with content..."
                    />
                    {!!searchValue && (
                        <button onClick={handleClear} className={cx('clear')}>
                            <FontAwesomeIcon icon={faClose} />
                        </button>
                    )}
                    <button className={cx('search-btn')}>
                        <FontAwesomeIcon icon={faSearch} />
                    </button>
                </div>

                <div className={cx('action')}>
                    {
                        currentUser ? (
                            <>
                                <Tippy content="Create new post" placement='bottom'>
                                    <Button primary round iconLeft={faPlus}>Create</Button>
                                </Tippy>
                                <Button className='notify' iconLeft={faBell} />
                                <img src={images.avatar} alt="Avatar" className={cx('current-user')} />
                            </>
                        ) : (
                            <>
                                <Button primary round >Login</Button>
                                <Button primary round >Register</Button>
                            </>
                        )
                    }

                </div>
            </div>
        </header>
    );
}

export default Header;
