import styles from '~/styles/share.module.scss'

function useValidator(options) {

    var formElement = document.querySelector(options.form)
    if (formElement) {
        options.rules.forEach((rule) => {
            var inputElement = formElement.querySelector(rule.selector)
            var errorElement = inputElement.parentElement.querySelector(`.${styles.formMessage}`)
            if (inputElement) {
                inputElement.onblur = () => {
                    var errorMessage = rule.test(inputElement.value)
                    if (errorMessage) {
                        errorElement.innerText = errorMessage;
                        inputElement.parentElement.classList.add(styles.invalid);
                    } else {
                        // Nếu không có lỗi, xóa thông báo và class invalid
                        errorElement.innerText = '';
                        inputElement.parentElement.classList.remove(styles.invalid);
                    }
                }
            }
        })
    }

}

useValidator.isRequired = (selector) => {
    return {
        selector,
        test: (value) => {
            return value.trim() ? undefined : 'Trường này là bắt buộc'
        }
    }
}

export default useValidator;
