import * as request from '~/utils/request'

const register = async (data) => {
    try {
        const result = await request.post('users', data)
        const { code, message } = result.response.data;

        switch (code) {
            case 40001:
            case 40002:
                console.log(message); // Log thông báo lỗi
                return { success: false, message }; // Trả về thông báo lỗi
            default:
                return result; // Trả về kết quả khi không có lỗi
        }
    } catch (error) {
        console.log(error);
    }
}

export default register;
