import { useState } from 'react';

const useValidator = (options) => {
    const [errors, setErrors] = useState({}); // Lưu trạng thái lỗi cho các trường

    const validateField = (fieldName, value) => {
        const rule = options.test.find((rule) => rule.selector === fieldName);
        if (rule) {
            const errorMessage = rule.test(value); // Gọi hàm kiểm tra
            setErrors((prev) => ({
                ...prev,
                [fieldName]: errorMessage, // Cập nhật lỗi cho trường
            }));

        }
    };

    const clearError = (fieldName) => {
        setErrors((prev) => ({
            ...prev,
            [fieldName]: undefined, 
        }));
    };

    const validateAll = (formData) => {
        const newErrors = {};
        options.rules.forEach((rule) => {
            const value = formData[rule.selector];
            console.log(value);

            const errorMessage = rule.test(value);
            if (errorMessage) {
                newErrors[rule.selector] = errorMessage;
            }
        });

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0; 
    };

    return {
        errors,
        validateField,
        clearError,
        validateAll,
    };
};

useValidator.isRequired = (selector) => {
    return {
        selector,
        test: (value) => {
            return value.trim() ? undefined : 'Trường này là bắt buộc';
        },
    };
};

export default useValidator;
