import classNames from "classnames/bind";
import styles from './ActiveAccount.module.scss'
import stylesGrid from '~/styles/grid.module.scss' 
import Button from "~/components/Button";
import Image from "~/components/Image";
import images from "~/assets/images";
import { useRef } from "react";

const cx = classNames.bind(styles)


function ActiveAccount() {

    const headingRef = useRef();
    const titleRef = useRef();

    const handleSendEmail = () => {
        headingRef.current.innerText = 'Check Email'
    }

    return (
        <div className={`${cx('wrapper')} ${stylesGrid.grid}`}>
            <div className={` ${stylesGrid['grid__row-6']} ${cx('logo')}`}>
                <Image className={cx('logo-img')} src={images.logo} alt='Logo'/>
            </div>
            <div className={` ${stylesGrid['grid__row-6']} ${cx('content')} `}>
                <div className={cx('header')}>
                    <h4 ref={headingRef} className={cx('heading')}>Active Your Account</h4>  
                    <p ref={titleRef} className={cx('title')}>To active your account, we need to verify your email. Please press the send button and check your email.</p>
                </div>
                <div className={cx('body')}>
                    <div className={cx('send')}>
                        <Button onClick={handleSendEmail} className={cx('btn-send')} primary round>Send</Button>
                        <span className={cx('time-send')}>90s</span>
                    </div>
                    <div className={cx('other')}>
                        <span className={cx('link')}>Sign in with another account?</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ActiveAccount;
