import { useState } from 'react';
import stylesGrid from '~/styles/grid.module.scss';
import styles from '~/styles/share.module.scss';
import images from '~/assets/images';
import Image from '~/components/Image';
import { useValidator } from '~/hooks';

function Register() {

    const [formData, setFormData] = useState({
        fullname: '',
        email: '',
        language: '',
        gender: '',
        username: '',
        password: '',
        password_confirm: ''
    });

    const { errors, validateField, clearError, validateAll } = useValidator({
        rules: [
            useValidator.isRequired('fullname'),
            useValidator.isRequired('email'),
            useValidator.isEmail('email'),
            useValidator.isRequired('language'),
            useValidator.isRequired('gender'),
            useValidator.isRequired('username'),
            useValidator.isRequired('password'),
            useValidator.minLength('password', 5),
            useValidator.isRequired('password_confirm'),
            useValidator.isPasswordMatch('password_confirm', 'password'),
        ]
    });

    const fetchApi = async ()

    const handleChange = e => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
        clearError(name);
    };

    const handleBlur = e => {
        validateField(e.target.name, formData);
    };

    const handleSubmit = e => {
        e.preventDefault();
        if (validateAll(formData)) {
            console.log('Form submitted:', formData);
        } else {
            console.log('Có lỗi xảy ra:', errors);
        }
    };

    return (
        <div className={styles.wrapper}>
            <div className={`${stylesGrid.grid} ${styles.register}`}>
                <div className={`${stylesGrid['grid__row-6']} ${styles.registerLogo}`}>
                    <Image className={styles.img} src={images.logo} />
                </div>
                <div className={`${stylesGrid['grid__row-6']} ${styles.registerContent}`}>
                    <form className={styles.form} id="form-1">
                        <h3 className={styles.heading}>Register</h3>
                        <p className={styles.desc}>Welcome To Forum Language</p>

                        <div className={styles.spacer}></div>

                        {/* Fullname */}
                        <div className={`${styles.formGroup} ${errors.fullname ? styles.invalid : ''}`}>
                            <label htmlFor="fullname" className={styles.formLabel}>Full Name</label>
                            <input
                                id="fullname"
                                name="fullname"
                                type="text"
                                placeholder="Ex: Nguyen Van A"
                                className={styles.formControl}
                                value={formData.fullname}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                            {errors.fullname && <span className={styles.formMessage}>{errors.fullname}</span>}
                        </div>

                        {/* Email */}
                        <div className={`${styles.formGroup} ${errors.email ? styles.invalid : ''}`}>
                            <label htmlFor="email" className={styles.formLabel}>Email</label>
                            <input
                                id="email"
                                name="email"
                                type="text"
                                placeholder="Ex: outlook@domain.com"
                                className={styles.formControl}
                                value={formData.email}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                            {errors.email && <span className={styles.formMessage}>{errors.email}</span>}
                        </div>

                        {/* Language */}
                        <div className={`${styles.formGroup} ${errors.language ? styles.invalid : ''}`}>
                            <label htmlFor="language" className={styles.formLabel}>Language</label>
                            <select
                                className={styles.formControl}
                                id="language"
                                name="language"
                                value={formData.language}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            >
                                <option value="" disabled>Select your language</option>
                                <option value="English">English</option>
                                <option value="China">China</option>
                                <option value="Japan">Japan</option>
                            </select>
                            {errors.language && <span className={styles.formMessage}>{errors.language}</span>}
                        </div>

                        {/* Gender */}
                        <div className={`${styles.formGroup} ${errors.gender ? styles.invalid : ''}`}>
                            <label htmlFor="gender" className={styles.formLabel}>Gender</label>
                            <select
                                className={styles.formControl}
                                id="gender"
                                name="gender"
                                value={formData.gender}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            >
                                <option value="" disabled>Select your gender</option>
                                <option value="Male">Male</option>
                                <option value="Female">Female</option>
                                <option value="Other">Other</option>
                            </select>
                            {errors.gender && <span className={styles.formMessage}>{errors.gender}</span>}
                        </div>

                        {/* Username */}
                        <div className={`${styles.formGroup} ${errors.username ? styles.invalid : ''}`}>
                            <label htmlFor="username" className={styles.formLabel}>Username</label>
                            <input
                                id="username"
                                name="username"
                                type="text"
                                placeholder="Ex: maivanloi"
                                className={styles.formControl}
                                value={formData.username}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                            {errors.username && <span className={styles.formMessage}>{errors.username}</span>}
                        </div>

                        {/* Password */}
                        <div className={`${styles.formGroup} ${errors.password ? styles.invalid : ''}`}>
                            <label htmlFor="password" className={styles.formLabel}>Password</label>
                            <input
                                id="password"
                                name="password"
                                type="password"
                                placeholder="Password"
                                className={styles.formControl}
                                value={formData.password}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                            {errors.password && <span className={styles.formMessage}>{errors.password}</span>}
                        </div>

                        {/* Password Confirm */}
                        <div className={`${styles.formGroup} ${errors.password_confirm ? styles.invalid : ''}`}>
                            <label htmlFor="password_confirm" className={styles.formLabel}>Password Confirm</label>
                            <input
                                id="password_confirm"
                                name="password_confirm"
                                placeholder="Password Confirm"
                                type="password"
                                className={styles.formControl}
                                value={formData.password_confirm}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                            {errors.password_confirm && <span className={styles.formMessage}>{errors.password_confirm}</span>}
                        </div>

                        {/* Submit Button */}
                        <button onClick={handleSubmit} className={styles.formSubmit}>Register</button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Register;
