function useValidator(options) {

    var formElement = document.querySelector(options.form)

    if(formElement) {
        var inputElement = formElement.querySelector(rule)
    }

}

useValidator.isRequired = (selector) => {
    return {
        selector,
        rules: (value) => {
            return !!value ? undefined : 'Trường này là bắt buộc'
        }
    }
}

export default useValidator;
