import classNames from "classnames/bind"
import { Link } from "react-router-dom"

import styles from "./Button.module.scss"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"


const cx = classNames.bind(styles)

function Button({ className, children, to, href, onClick, primary = false, round = false, iconLeft = false, iconRight = false, ...passProps }, ref) {
    let Comp = 'button'
    const props = {
        onClick,
        ...passProps
    }

    const classes = cx('wrapper', {
        [className]: className,
        round,
        primary,
    })

    if (to) {
        Comp = Link
        props.to = to
    } else if (href) {
        Comp = 'a'
        props.href = href
    }
    return (
        <Comp ref={ref} className={classes} {...props}>
            {iconLeft && <FontAwesomeIcon className={cx('icon')} icon={iconLeft} />}
            {children && <span className={cx('title')}>{children}</span>}
            {iconRight && <FontAwesomeIcon className={cx('icon')} icon={iconRight} />}
        </Comp>
    );
}

export default Button;
