import { useState } from 'react';

const useValidator = (options) => {
    const [errors, setErrors] = useState({});

    const validateField = (fieldName, value) => {
        const rule = options.rules.find((rule) => rule.selector === fieldName);
        if (rule) {
            const errorMessage = rule.test(value);
            setErrors((prev) => ({
                ...prev,
                [fieldName]: errorMessage,
            }));

        }
    };

    const clearError = (fieldName) => {
        setErrors((prev) => ({
            ...prev,
            [fieldName]: undefined,
        }));
    };

    const validateAll = (formData) => {
        const newErrors = {};
        options.rules.forEach((rule) => {
            const value = formData[rule.selector];

            const errorMessage = rule.test(value);
            if (errorMessage) {
                newErrors[rule.selector] = errorMessage;
            } else {

            }
        });

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    return {
        errors,
        validateField,
        clearError,
        validateAll,
    };
};

useValidator.isRequired = (selector) => {
    return {
        selector,
        test: (value) => {
            return value && value.trim() ? undefined : 'Trường này là bắt buộc';
        },
    };
};

useValidator.isPasswordMatch = (selector, compareSelector) => {
    return {
        selector,
        test: (value, formData) => {
            return value && value.trim() === formData[compareSelector] ? undefined : 'Mật khẩu không khớp';
        },
    };
};

useValidator

export default useValidator;
