import * as request from '~/utils/request'

const search = async (page, size = 4, content = '', language = '') => {
    try {
        const result = await request.get('posts', {
            params: {
                page,
                size,
                content,
                language
            }
        })

        return result.result.content;
    } catch (error) {

    }
}

export default search;
