var inherits              = require('util').inherits
  , Readable              = require('stream').Readable
  , ReadableAsyncKit      = require('asynckit/lib/readable_asynckit')
  , ReadableParallel      = require('asynckit/lib/readable_parallel')
  , ReadableSerial        = require('asynckit/lib/readable_serial')
  , ReadableSerialOrdered = require('asynckit/lib/readable_serial_ordered')
  ;

// API
module.exports =
{
  parallel      : ReadableParallel,
  serial        : ReadableSerial,
  serialOrdered : ReadableSerialOrdered, 
};

inherits(ReadableAsyncKit, Readable);

inherits(ReadableParallel, ReadableAsyncKit);
inherits(ReadableSerial, ReadableAsyncKit);
inherits(ReadableSerialOrdered, ReadableAsyncKit);
